mapper.insert:
INSERT INTO sf_bloc
( name
, is_deleted
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES ( #{name}
       , #{isDeleted}
       , #{remark}
       , #{createdBy}
       , #{createdDate}
       , #{lastModifiedBy}
       , #{lastModifiedDate})

INSERT INTO sf_bloc
( id
, name
, is_deleted
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES ( #{id}
       , #{name}
       , #{isDeleted}
       , #{remark}
       , #{createdBy}
       , #{createdDate}
       , #{lastModifiedBy}
       , #{lastModifiedDate})

mapper.update:
UPDATE sf_bloc
SET name               = #{name}
  , remark             = #{remark}
  , last_modified_by   = #{lastModifiedBy}
  , last_modified_date = #{lastModifiedDate}
WHERE id = #{id}

UPDATE sf_bloc
SET is_deleted         = #{isDeleted}
  , deleted_date       = #{deletedDate}
  , deleted_by         = #{deletedBy}
  , last_modified_by   = #{lastModifiedBy}
  , last_modified_date = #{lastModifiedDate}
WHERE id = #{id}
