mapper.insert:
INSERT INTO sf_calendar
( date
, is_holiday
, holiday_name
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES 

( #{item.date}
, #{item.isHoliday}
, #{item.holidayName}
, #{item.remark}
, #{item.createdBy}
, #{item.createdDate}
, #{item.lastModifiedBy}
, #{item.lastModifiedDate})

update.foreach:
UPDATE sf_calendar
SET is_holiday         = #{item.isHoliday}
  , holiday_name       = #{item.holidayName}
  , remark             = #{item.remark}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE date = #{item.date}
