mapper.insert:
INSERT INTO sf_dept
( name
, parents_id
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES

  ( #{item.name}
  , #{item.parentsId}
  , #{item.remark}
  , #{item.createdBy}
  , #{item.createdDate}
  , #{item.lastModifiedBy}
  , #{item.lastModifiedDate})

INSERT INTO sf_dept
( id
, name
, parents_id
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES

  ( #{item.id}
  , #{item.name}
  , #{item.parentsId}
  , #{item.remark}
  , #{item.createdBy}
  , #{item.createdDate}
  , #{item.lastModifiedBy}
  , #{item.lastModifiedDate})

update.foreach:
UPDATE sf_dept
SET name               = #{item.name}
  , parents_id         = #{item.parentsId}
  , remark             = #{item.remark}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE id = #{item.id}
