INSERT INTO sf_code
( id
, group_code_id
, code
, name
, sort_sequence
, is_used
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES

  ( #{item.id}
  , #{item.groupCodeId}
  , #{item.code}
  , #{item.name}
  , #{item.sortSequence}
  , #{item.isUsed}
  , #{item.remark}
  , #{item.createdBy}
  , #{item.createdDate}
  , #{item.lastModifiedBy}
  , #{item.lastModifiedDate})

update.foreach:
UPDATE sf_code
SET group_code_id      = #{item.groupCodeId}
  , code               = #{item.code}
  , name               = #{item.name}
  , sort_sequence      = #{item.sortSequence}
  , is_used            = #{item.isUsed}
  , remark             = #{item.remark}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE id = #{item.id}
