mapper.insert:
INSERT INTO sf_plan
( start_date
, start_hour
, start_minute
, end_date
, end_hour
, end_minute
, plan_title
, plan_contents
, plan_color
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES 

( #{item.startDate}
, #{item.startHour}
, #{item.startMinute}
, #{item.endDate}
, #{item.endHour}
, #{item.endMinute}
, #{item.planTitle}
, #{item.planContents}
, #{item.planColor}
, #{item.createdBy}
, #{item.createdDate}
, #{item.lastModifiedBy}
, #{item.lastModifiedDate})

update.foreach:
    UPDATE sf_plan
    SET start_date         = #{item.startDate}
	    , start_hour         = #{item.startHour}
	    , start_minute       = #{item.startMinute} 
	    , end_date           = #{item.endDate} 
	    , end_hour           = #{item.endHour}
	    , end_minute         = #{item.endMinute}
	    , plan_title         = #{item.planTitle}
	    , plan_contents      = #{item.planContents}
	    , plan_color         = #{item.planColor}
      , last_modified_by   = #{item.lastModifiedBy}
      , last_modified_date = #{item.lastModifiedDate}
    WHERE id = #{item.id}

