mapper.select:
SELECT id , name , remark , created_by , created_date , last_modified_by , last_modified_date FROM sf_role WHERE 1=1
AND id = #{id}
AND name LIKE CONCAT('%', #{name}, '%')

mapper.insert:
INSERT INTO sf_role
( name
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES

( #{item.name}
, #{item.remark}
, #{item.createdBy}
, #{item.createdDate}
, #{item.lastModifiedBy}
, #{item.lastModifiedDate})

INSERT INTO sf_role
( id
, name
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES 

( #{item.id}
, #{item.name}
, #{item.remark}
, #{item.createdBy}
, #{item.createdDate}
, #{item.lastModifiedBy}
, #{item.lastModifiedDate})

update.foreach:
UPDATE sf_role
SET name               = #{item.name}
  , remark             = #{item.remark}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE id = #{item.id}
